import React, { useEffect } from 'react'
import {useDispatch, useSelector} from 'react-redux'
import { Row, Col } from 'react-bootstrap'
import Result from './Result'
import Loader from './Loader'
import Message from './Message'
import {resultList} from '../actions/searchActions'

const DisplayContent = (searchString) => {
  const dispatch = useDispatch()

  const listResult = useSelector(state => state.listResult)
  const { loading, error, items } = listResult

  useEffect(() => {
    dispatch(resultList(searchString))
  }, [dispatch])

  if(items && items.data && items.data.items){
      console.log(items.data.items)
  }

  return (
    <>
      {loading ? (
        <Loader />
      ) : error ? <Message variant='danger'>{error}</Message> : 
      <Row>
        {items.map((item) => (
          <Col key={item._id} sm={12} md={6} lg={4} xl={3}>
            <Result item={item} />
          </Col>
        ))}
      </Row>
      }
    </>

  )
}

export default DisplayContent
