import DisplayContent from './DisplayContent'
import {useDispatch, useSelector} from 'react-redux'
import {resultList} from '../actions/searchActions'

const Search = () => {
    const dispatch = useDispatch()
    const [title, setTitle] = useState('')
    let search = ''

    if(title.length >= 8){
        search = title
    }


    useEffect(() => {
        dispatch(resultList(search))
        }, [dispatch])


    return (
        <>
        <h1>Myndaleit</h1>
            <input type="text" className="form-control" name="searchWords" id="keywords" placeholder="leitarorÃ°" onChange={event => setTitle(event.target.value)}/>

        {title && (
            <DisplayContent />
        )}
        </>
    )}

export default Search
